INSERT INTO `content_holders` VALUES (1,'Header (do not remove)','<div id=\"placesocial\">\r\n        <a href=\"/contacts/newsletter_signup\"><img src=\"/images/btn_newsletter.gif\" /></a> \r\n        <a href=\"/donate\"><img src=\"/images/btn_make_a_donation.gif\" /></a>\r\n</div>\r\n\r\n<div id=\"placemainmenu\">\r\n        <div id=\"placemainmenu\">\r\n                <ul id=\"mainmenu\">\r\n                        <li>\r\n                                <a href=\"/\">Home</a>\r\n                        </li>\r\n                        <li>\r\n                                <a href=\"/programs\">Our Programs</a>\r\n                        </li>\r\n                        <li>\r\n                                <a href=\"/news\">News</a>\r\n                                <ul>\r\n                                        <li>\r\n                                                <a href=\"/news/releases\">Recent posts</a>\r\n                                        </li>\r\n                                        <li>\r\n                                                <a href=\"/news/newsletters\">CAM Newsletters</a>\r\n                                        </li>\r\n                                </ul>\r\n                        </li>\r\n                        <li>\r\n                                <a href=\"/donate\">Donate</a>\r\n                        </li>\r\n                        <li>\r\n                                <a href=\"/about-cam/who-we-are\">About Us</a>\r\n                                <ul>\r\n                                        <li>\r\n                                                <a href=\"/about-cam/who-we-are\">Who we are</a>\r\n                                        </li>\r\n                                        <li>\r\n                                                <a href=\"/about-cam/locations\">Locations</a>\r\n                                        </li>\r\n                                        <li>        <a href=\"/about-cam/financial-accountability\">Financial Accountability</a>\r\n                                                <ul>\r\n                                                        <li>\r\n                                                                <a href=\"/about-cam/financial-accountability/financial-statements\">Financial Statements</a>\r\n                                                        </li>\r\n                                                </ul>\r\n                                        </li>\r\n                                        <li>\r\n                                                <a href=\"/about-cam/statement-of-faith\">Statement of Faith</a>\r\n                                        </li>\r\n                                        <li>\r\n                                                <a href=\"/about-cam/biblical-stewardship-services\">Biblical Stewardship Services</a>\r\n                                        </li>\r\n                                        <li>\r\n                                                <a href=\"/about-cam/tgs-international\">TGS International</a>\r\n                                        </li>\r\n                                </ul>\r\n                        </li>\r\n                        <li>\r\n                                <a href=\"/contacts/new\">Contact Us</a>\r\n                        </li>\r\n                        <div>\r\n                                <form id=\"frmsearch\" accept-charset=\"UTF-8\" action=\"/search\" method=\"post\">\r\n                                        <input name=\"utf8\" value=\"?\" type=\"hidden\" />\r\n                                        <input name=\"authenticity_token\" value=\"Vz1EFjKUWJbBQx7qXoLHmhK6jfetnglnhcvOVhCunQw=\" type=\"hidden\" />\r\n                                        <input id=\"query\" name=\"query\" class=\"textboxsearch\" type=\"search\" />\r\n                                        <input class=\"submitsearch\" name=\"commit\" value=\"Search!\" type=\"submit\" />\r\n                                </form>\r\n                        </div>\r\n                </ul>\r\n        </div>\r\n</div>\r\n',0,'2011-08-11 20:26:55','2012-01-03 16:08:13'),(2,'Footer (do not remove)','		<p><a href=\"/site-map\">site map</a> |\r\n			<a href=\"/contacts/new\">contact</a> |\r\n			<a href=\"/privacy-policy\">privacy policy</a> |\r\n                        <a href=\"/terms-and-conditions\">terms and conditions</a> |\r\n                        <a href=\"/solicitation-disclosure\">solicitation disclosure</a>\r\n		</p>\r\n	<p>\r\n	  Christian Aid Ministries, 2011</p>',0,'2011-08-11 19:30:57','2012-02-28 16:32:12'),(5,'RRT Sidebar','<div class=\"boxnav\">\r\n<h3>Rapid Response Team</h3>\r\n<p>Currently we are looking for volunteers in the PA area to help clean up flooded houses from Hurricane Irene. If you&#39;d like to volunteer or schedule a volunteer group, please contact <em>Susan</em> at 330-893-2428. </p>\r\n</div>',0,'2011-08-09 15:38:17','2011-09-02 19:37:29'),(6,'attention_farmers','<div class=\"boxnav\">\r\n								<h3>Attention farmers!</h3>\r\n<p>Did you know you could make tax-deductible donations of grain or other commodities to Christian Aid Ministries? We can arrange for your grain donation to be received by the local mill or grain elevator of your choice. For more information, call the CAM office<strong> (330-893-2428)</strong> and ask for <em>Paul A. Miller </em>or <em>Eli Weaver</em>.</p>\r\n<div class=\"clear\"></div>\r\n							</div>',1,'2011-08-09 15:38:43','2011-09-09 19:11:17'),(8,'homepage_sidebar','<p>&#160;{{ content_holder_103 }}{{ content_holder_105 }}{{ content_holder_88 }}{{ content_holder_100 }}</p>',3,'2011-08-09 15:40:37','2012-03-19 16:51:50'),(9,'Open House','<div class=\"boxnav\">\r\n<h3>Annual CAM Open House</h3>\r\n\r\n<p><strong>When:</strong> November 4-5, 2011<strong><br />Where:</strong> CAM warehouse 2412 Division Highway Ephrata, PA 17522 Phone: 717-354-2434</p>\r\n<p>We invite you to join us at the 2011 annual CAM Open House. This year it will again be held at the CAM warehouse in Ephrata, Pennsylvania.<br />Open House includes a two-hour slide presentation on Friday and Saturday evening (same slides both evenings), reports from a number of overseas field staff, self-guided tours of the CAM warehouse, and a Biblical Stewardship seminar on Saturday morning. <br />A large display of Christian books and CDs will be available for purchase at special Open House prices Friday evening through Saturday evening.</p>\r\n<h3>We hope to see you there!</h3>\r\n</div>',4,'2011-08-11 18:53:18','2011-09-06 12:52:45'),(10,'Donation Complete Thank You Page ( DO NOT REMOVE )','<h1>Thank you for sharing!</h1>\r\n\r\n\r\n<h2><img src=\"/system/images/BAhbBlsHOgZmSSIpMjAxMi8wMy8wMi8yMF81Nl8wMl8zMzNfdHlfbWFyXzMucG5nBjoGRVQ/ty_mar_3.png\" height=\"454\" width=\"344\" /></h2>\r\n<h2>Your donations make a big difference! </h2>\r\n<p>Like a ray of sunshine on a cloudy day, your gift of compassion brings hope to many needy souls. With your support, we are able to provide suffering people with medicines, vitamins, and medical supplies through <a title=\"/programs/19\" href=\"/programs/19\">Medicines-for-Multitudes</a>. Your donations also help feed the famished, cloth the impoverished, provide spiritual food through Bibles and Christian literature, and in many other ways bring light to gloomy lives.</p>\r\n<h3>Thank you for letting God use you! God bless you!</h3>\r\n<h3>Please check your email for your transaction information.</h3>\r\n',NULL,'2011-09-07 21:28:59','2012-03-02 17:58:09'),(11,'Programs Listing','<div class=\"boxnav\">\r\n<h1>Other Programs</h1>\r\n    <ul id=\"donations\">\r\n        <li>\r\n          <a href=\"/donations/5\">Bibles-for-the-World</a>\r\n        </li>\r\n        <li>\r\n          <a href=\"/donations/6\">Billboard Evangelism</a>\r\n        </li>\r\n        <li>\r\n          <a href=\"/donations/3\">Christian Family Magazines</